function calculateError (originalImg, decompressedImg)    % lemos as imagens    oimg = imread(originalImg);    dimg = imread(decompressedImg);        % fazemos uma conversão simples pra não bugar o MATLAB    oimg = double(oimg);    dimg = double(dimg);        % calculamos o erro relativo com a função norm()    errR = norm(oimg(:,:,1) - dimg(:,:,1)) / norm(oimg(:,:,1));    errG = norm(oimg(:,:,2) - dimg(:,:,2)) / norm(oimg(:,:,2));    errB = norm(oimg(:,:,3) - dimg(:,:,3)) / norm(oimg(:,:,3));        % calculamos a média dos erros e exibimos    err = (errR + errG + errB) / 3;    disp(err);