function compress (originalImg, k)    % ler imagem e calcular o tamanho dela    img = imread(originalImg);    p = size(img, 1);            % pegar só as linhas e colunas i tais que i = 1 mod (k+1)    newImg = img(1:(k+1):p, 1:(k+1):p, :);            % verificamos se a imagem pode ser comprimida    n = size(newImg, 1);    if p != n + (n-1)*k        fprintf("Não dá para comprimir a imagem com esse k!\n");        return;    end        % construir a imagem comprimida    imwrite(newImg, "out_test/jungle/compressed_k1-3.png", "Quality", 100);    end